INSERT INTO pages VALUES(2,'FAQ','faq',replace(replace('                <!-- Question 1 -->\r\n                <div class="border-b border-gray-100 pb-6">\r\n                    <h3 class="text-xl font-semibold text-gray-900 mb-4">What kind of churches do you list?</h3>\r\n                    <p class="text-gray-700 leading-relaxed">\r\n                        We list evangelical Christian churches that are, to the best of our knowledge, committed to the Trinity, the authority and sufficiency of Scripture, the substitutionary death and bodily resurrection of Jesus Christ, salvation by grace alone through faith, a biblical view of sexuality, and the complementary roles of men and women.\r\n                    </p>\r\n                </div>\r\n\r\n                <!-- Question 2 -->\r\n                <div class="border-b border-gray-100 pb-6">\r\n                    <h3 class="text-xl font-semibold text-gray-900 mb-4">How do I choose a church?</h3>\r\n                    <div class="space-y-4">\r\n                        <p class="text-gray-700 leading-relaxed">\r\n                            We encourage you to practice discernment.\r\n                        </p>\r\n                        \r\n                        <div class="bg-blue-50 border-l-4 border-blue-400 p-4 rounded-r-md">\r\n                            <p class="text-gray-700 mb-3">Here are some helpful resources:</p>\r\n                            <ul class="space-y-2 text-gray-700">\r\n                                <li class="flex items-start">\r\n                                    <span class="text-blue-500 mr-2">•</span>\r\n                                    <span>See <a href="https://drbc.us/resources/considerations-for-finding-a-good-church/" target="_blank" class="text-blue-600 hover:text-blue-800 underline">Considerations for Finding a Good Church</a>, written by Desert Ridge Baptist Church of St. George, Utah.</span>\r\n                                </li>\r\n                                <li class="flex items-start">\r\n                                    <span class="text-blue-500 mr-2">•</span>\r\n                                    <span>Utah''s own Jonathan Stoddard has written, <a href="https://www.amazon.com/Choosing-Church-Biblical-Practical-Guide/dp/0997398485" target="_blank" class="text-blue-600 hover:text-blue-800 underline italic">Choosing A Church: A Biblical and Practical Guide</a>.</span>\r\n                                </li>\r\n                                <li class="flex items-start">\r\n                                    <span class="text-blue-500 mr-2">•</span>\r\n                                    <span>Many have found <a href="https://www.9marks.org/about/#:~:text=or%20assumed%20them.-,The%20Nine%20Marks,-Expositional%20Preaching" target="_blank" class="text-blue-600 hover:text-blue-800 underline italic">9 Marks of a Healthy Church</a> to be a helpful guide.</span>\r\n                                </li>\r\n                            </ul>\r\n                        </div>\r\n                    </div>\r\n                </div>\r\n\r\n                <!-- Question 3 -->\r\n                <div class="">\r\n                    <h3 class="text-xl font-semibold text-gray-900 mb-4">How can I submit church data?</h3>\r\n                    <p class="text-gray-700 leading-relaxed">\r\n                        You can submit church data <a href="/suggest-church" target="_blank" class="inline-flex items-center px-3 py-1 rounded-md bg-green-100 text-green-800 hover:bg-green-200 transition-colors duration-200 no-underline ml-1">here</a>.\r\n                    </p>\r\n                </div>','\r',char(13)),'\n',char(10)),'83b9fa2e-50bc-4c6d-3a77-d75312039400','https://imagedelivery.net/a9qGInTN4zENiufwTmRRwQ/83b9fa2e-50bc-4c6d-3a77-d75312039400/large',2,1751151610,1752018151);
